mapper.select:
SELECT * FROM cetide_blog.article WHERE deleted = 0 AND user_id = #{id}
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') OR category LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY created_at DESC

mapper.update:
UPDATE cetide_blog.article
SET deleted = 1
WHERE id = #{id}

UPDATE cetide_blog.article
SET view_count = #{count}
WHERE id = #{articleId}
